export default {
	formatShipmentItemsTable: () => {
		var ret = []
		for (let i = 0; i < GetShipmentDetail.data.data.getShipment.items.length; i ++) {
			let item = GetShipmentDetail.data.data.getShipment.items[i]
			let line = {"id": item.id}
			if (item.material != null) {
				line.material = item.material.code
			} else {
				line.material = ""
			}
			if (item.bin != null) {
				line.bin = item.bin.name
			} else {
				line.bin = ""
			}
			if (item.plannedQuantity != null) {
				line.quantity = item.plannedQuantity
			} else {
				line.quantity = 0
			}
			ret.push(line)
		}
		return ret
	},
	materialValue: () => {
		if (tblShipmentItems.selectedRow.material != "") {
			return GetShipmentDetail.data.data.getShipment.items[tblShipmentItems.selectedRowIndex].material.id
		} 
		return ""
	},
	binValue: () => {
		if (tblShipmentItems.selectedRow.bin != "") {
			return GetShipmentDetail.data.data.getShipment.items[tblShipmentItems.selectedRowIndex].bin.id
		} 
		return ""
	},
	quantityValue: () => {
		if (tblShipmentItems.selectedRow.quantity != 0) {
			return GetShipmentDetail.data.data.getShipment.items[tblShipmentItems.selectedRowIndex].plannedQuantity
		} 
		return ""
	},
}
