export default {
	siteChange: (siteId) => {
		if(!_.isEmpty(siteId)){
			GetSiteWarehouses.run()
		}
	},
	warehouseChange: (warehouseId) => {
		if(!_.isEmpty(warehouseId)){
			queryCarriers.run()
		}
	},
	clearValues:() => {
		storeValue("getWarehouseByNameResult",undefined)
		storeValue("getCarrierByCodeResult",undefined)
		storeValue("getSubLotByCode",undefined)
		inpBarcodeScan.text = undefined
	}, 
	barcodeValueChange: (barcode) => {
		if(!_.isEmpty(barcode)){
		getSubLotByCode.run(()=>{
			if(getSubLotByCode.data.data.getStockOnHand==null){
				getCarrierByCode.run(()=>{
					if (getCarrierByCode.data.data.getCarrier==null){
							getWarehouseByName.run(()=>{
						if (getWarehouseByName.data.data.queryEquipment.length == 0){
						}else{
							storeValue("getWarehouseByNameResult",getWarehouseByName.data.data.queryEquipment[0])
							inpBarcodeScan.text = undefined
						}
					})
				}
					else{
						storeValue("getCarrierByCodeResult",getCarrierByCode.data.data.getCarrier)
						inpBarcodeScan.text = undefined
					}
				})
			}else{
				storeValue("getSubLotByCode",getSubLotByCode.data.data.getStockOnHand[1])
				inpBarcodeScan.text = undefined
			}
		})
		}
	}
}
