export default {
	myVar1: [],
	myVar2: {},
	HandleUserSites: () => {
		if(queryUserSites.data.data.getUser.hasRole.length > 0){
			return queryUserSites.data.data.getUser.hasRole[0].responsibilities
		}else{
			return null
		}
	},
	HandleMoveCarrierToBinAvailability: () => {
		if(appsmith.store.queryCarrierID == "" || appsmith.store.queryBinId == ""){
			return true
		}else{
			return false
		}
	},
	HandleMoveItemAvailability: () => {
		if(appsmith.store.queryMaterialSubLotCode == "" || appsmith.store.queryCarrierID == ""){
			return true
		}else{
			return false
		}
	},
	ClearValueAfterMoveCarrierToBin: () => {
		storeValue("queryCarrierID","")
		storeValue("queryCarrierCode","")
		storeValue("queryBinId","")
		storeValue("queryBinName","")
	},
	ClearAllValue: () => {
		storeValue("queryCarrierID","")
		storeValue("queryCarrierCode","")
		storeValue("queryBinId","")
		storeValue("queryBinName","")
		storeValue("queryMaterialSubLotCode","")
	},
	HandleBarCodeInput: async () => {
		if(!_.isEmpty(barcodeInput.text)){
			queryMaterialSublot.run(()=>{
				if(queryMaterialSublot.data.data.getMaterialSubLot == null){
					queryBin.run(()=>{
						if (queryBin.data.data.queryEquipment.length == 0){
							queryCarrier.run(()=>{
								if (queryCarrier.data.data.getCarrier == null){
									showAlert("Barcode cannot be found.","error")
								}else{
									storeValue("queryCarrierID",queryCarrier.data.data.getCarrier.id)
									storeValue("queryCarrierCode",queryCarrier.data.data.getCarrier.code)
								}
							})
						}else{
							storeValue("queryBinId",queryBin.data.data.queryEquipment[0].id)
							storeValue("queryBinName",queryBin.data.data.queryEquipment[0].name)
						}
					})
				}else{
					storeValue("queryMaterialSubLotCode",queryMaterialSublot.data.data.getMaterialSubLot.code)
				}
			})
		}
	},
	HandleMoveCarrier: async () => {
		if(!_.isEmpty(CarrierInput.text)){
			queryMoveCarrier.run(()=>{
				if(queryMoveCarrier.data.data.getCarrier != null){
					storeValue("queryMoveCarrierCode",queryMoveCarrier.data.data.getCarrier.code)
					storeValue("queryMoveCarrierId",queryMoveCarrier.data.data.getCarrier.id)
				}
			})
		}
	}
}
