export default {

	createDelivery: () => {
		if (CreateDelivery.data.errors != null) {
			showAlert(CreateDelivery.data.errors[0].message)
		} else {
			GetWarehouseDeliveries.run()
			closeModal("modalCreateDelivery")
		}
	},
	createShipmentExistingOrder: () => {
		if (CreateShipmentExistingOrder.data.errors != null) {
			showAlert(CreateShipmentExistingOrder.data.errors[0].message)
		} else {
			GetDeliveryDetail.run()
			closeModal("modalAddShipment")
		}
	},
	createShipmentNewOrder: () => {
		if (CreateShipmentNewOrder.data.errors != null) {
			showAlert(CreateShipmentNewOrder.data.errors[0].message)
		} else {
			GetDeliveryDetail.run()
			closeModal("modalAddShipment")
		}
	},
	addShipmentItem: () => {
		if (AddShipmentItem.data.errors != null) {
			showAlert(AddShipmentItem.data.errors[0].message)
		} else {
			GetShipmentDetail.run()
		}
	},
	updateShipmentItem: () => {
		if (UpdateShipmentItem.data.errors != null) {
			showAlert(UpdateShipmentItem.data.errors[0].message)
		} else {
			GetShipmentDetail.run()
			closeModal("modalEditShipmentItem")
			showModal("modalEditShipment")
		}
	}
}
