export default {

	receiveSerialised: () => {
		if (ReceiveSerialised.data.errors != null) {
			showAlert(ReceiveSerialised.data.errors[0].message)
		} else {
			GetShipmentTransactions.run()
		}
	},
	receiveTrackedByCarrier: () => {
		if (ReceiveTrackedByCarrier.data.errors != null) {
			showAlert(ReceiveTrackedByCarrier.data.errors[0].message)
		} else {
			GetShipmentTransactions.run()
		}
	},
	receiveUntracked: () => {
		if (ReceiveUntracked.data.errors != null) {
			showAlert(ReceiveUntracked.data.errors[0].message)
		} else {
			GetShipmentTransactions.run()
		}
	},
	AddCarrierreceiveSerialised: () => {
		if (ReceiveSerialised.data.errors != null) {
			showAlert(ReceiveSerialised.data.errors[0].message)
		} else {
			GetShipmentTransactions.run()
		}
	},
	undoTransaction: () => {
		if (UndoTransaction.data.errors != null) {
			showAlert(UndoTransaction.data.errors[0].message)
		} else {
			GetShipmentTransactions.run()
		}
		closeModal("modalItemLocation")
	},
	completeShipment: () => {
		if (CompleteShipment.data.errors != null) {
			showAlert(CompleteShipment.data.errors[0].message)
		}
		GetWarehouseShipmentsInbound.run()
	},
	addCarrier: () => {
		if (AddCarrier.data.errors != null) {
			showAlert(CompleteShipment.data.errors[0].message)
		} else {
			GetCarrierCarriers.run()
		}
	},

}
