actionConfiguration.body:
{{
JSON.stringify({
    variables:  {
	"input": SiteSelect.selectedOptionValue
},
    query: `
query($input:ID!){
  getEquipment(id:$input){
    name
    children(filter:{equipmentLevel:{eq:Warehouse}}){
				value: id
      label: name
      equipmentLevel
    }
  }
}
    `
})
}}

