actionConfiguration.body:
() => {
		if (checkEnterOrder.isChecked) {
			return CreateShipmentNewOrder.run()
			.then(()=>{
				if (CreateShipmentNewOrder.data.errors != null) {
					showAlert(CreateShipmentNewOrder.data.errors[0].message)
				} else {
					GetDeliveryDetail.run()
					closeModal("modalAddShipment")
				}				
			})
		} else {
			return CreateShipmentExistingOrder.run()
			.then(()=>{
				if (CreateShipmentExistingOrder.data.errors != null) {
					showAlert(CreateShipmentExistingOrder.data.errors[0].message)
				} else {
					GetDeliveryDetail.run()
					closeModal("modalAddShipment")
				}				
			})
		}
	}

