actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  input: [
  	{
			dateTimeCreatedUTC: moment(),
      customerReference: inputAddShipmentReference.text,
      warehouse: {id: selectWarehouse.selectedOptionValue},
      transportCompanyShipmentId: inputAddShipmentFromName.text,
      consignmentNumber: inpConsignmentNumber.text,
      type: "INBOUND",
			delivered: {"id": tblDeliveries.selectedRow.id},
			owner: {"id": selectPartner.selectedOptionValue},
			from: {"name": inputAddShipmentFromName.text},
      order: {
        id: selectOrderNumber.selectedOptionValue
      }
  	}
  ]
},
    query: `
mutation AddShipmentExistingOrder($input:[AddShipmentInput!]!) {
  addShipment(input:$input) {
    shipment {
      id
    }
  }
}
    `
})
}}

