{{
JSON.stringify({
    variables: {
			input: [{
				bin:{id:selectBin.selectedOptionValue},
				shipment:{id:tblShipments.triggeredRow.id},
				status:"CREATED",
				trackingDetailsArticleId:inpEditShipmentItemTracking.text
			}]
		},
    query: `
mutation AddShipmentItem($input:[AddShipmentItemInput!]!) {
  addShipmentItem(input:$input) {
    shipmentItem {
      id
    }
  }
}
    `
})
}}

