actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  input: [
  	{
			dateTimeCreatedUTC: moment(),
      customerReference: inputAddShipmentReference.text,
      warehouse: {id: selectWarehouse.selectedOptionValue},
      transportCompanyShipmentId: inputAddShipmentFromName.text,
      consignmentNumber: inpConsignmentNumber.text,
      type: "INBOUND",
			delivered: {"id": tblDeliveries.selectedRow.id},
			owner: {"id": selectPartner.selectedOptionValue},
			from: {"name": inputAddShipmentFromName.text},
      operationsRequest: {
        code: inpOrderNumber.text,
				operationsDefinition: {id:GetSegments.data.data.getOperationsDefinition.id},
				customer: {id: selectPartner.selectedOptionValue},
				equipment: {id: selectWarehouse.selectedOptionValue},
				operationsType: "Inventory",
        orderType: "PURCHASE_ORDER",
        createdDateTime: moment(),
        requestState: {name:"CREATED"},
				quantity:1,
				quantityUoM: {code:"EA"},
				segmentRequirements:[{
					code:inpOrderNumber.text+"-"+moment().unix(),
					operationsType: "Inventory",
        	requestState: {name:"CREATED"},
					segment: {id: selectAddShipmentSegment.selectedOptionValue},
					jobOrders:[{
						name: inpOrderNumber.text+"-"+moment().unix(),
						workType: "Inventory",
						createdDateTime: moment(),
						dispatchStatus: "CREATED",
						jobResponses:[{
							startDateTime: moment()
						}]
					}]
				}]
      }
  	}
  ]
},
    query: `
mutation AddShipmentNewOrder($input:[AddShipmentInput!]!) {
  addShipment(input:$input) {
    shipment {
      id
    }
  }
}
    `
})
}}

