actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectCustomer.selectedOptionValue
		},
    query: `
query($id:ID!){
  getPartner(id:$id){
    id
		name
    operationsRequests @cascade(fields:["requestState"]){
      code
      requestState(filter:{name:{in:["Unscheduled"]}}){name}
    }
  }
}
    `
})
}}

