{{
JSON.stringify({
    variables: {
			id:tblDeliveries.selectedRow.id
		},
    query: `
query GetDelivery($id:ID!){
  getDelivery(id:$id) {
    shipments {
			id
			operationsRequest{code}
      consignmentNumber
      transportCompanyShipmentId
      customerReference
			owner{name}
			from{name}
			itemsAggregate{count}
    }
  }
}
    `
})
}}

