actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input: {
				filter: {id: tblShipmentItems.selectedRow.id},
				set: {
					material:{id: selectShipmentItemMaterial.selectedOptionValue},
					bin:{id: selectBin.selectedOptionValue},
					plannedQuantity: parseInt(inputShipmentItemQuantity.text)
				}
			}
		},
    query: `
mutation UpdateShipmentItem($input:UpdateShipmentItemInput!){
  updateShipmentItem(input:$input){
    shipmentItem{
      id
    }
  }
}
    `
})
}}

