export default {
	siteChange: (siteId) => {
		if(!_.isEmpty(siteId)){
			GetSiteWarehouses.run()
		}
	},
	warehouseChange: (warehouseId) => {
		if(!_.isEmpty(warehouseId)){
			GetWarehouseBins.run()
			GetJobOrdersToReceive.run()
			GetMaterials.run()
			GetSegmentsReceiveStock.run()
		}
	},	
	customerChange: (customerId) => {
		if(!_.isEmpty(customerId)){
			GetJobOrdersToReceive.run()
			GetSegmentsReceiveStock.run()
			GetCustomerOrders.run()
		}
	},
	jobChange: () => {
		GetJobOrderMaterialReqs.run()
	},
	materialRequirementChange: (materialId) => {
		if (!_.isEmpty(materialId)){
			GetShipmentItemDetail.run()
			storeValue('receiveTabsDefault',Receive.shipmentItemReceiveType())
			.then(()=> {
				resetWidget('receiveTabs',true)
			})
			GetBulkStatus.run()
		} else {
			GetShipmentItemDetail.run()
			storeValue('receiveTabsDefault',"SERIALIZED")
			.then(()=> {
				resetWidget('receiveTabs',true)
			})
		}
	},
	submitSerial: () => { 
		if (!btnAddSerial.isDisabled){
			ReceiveSerialised.run()
			MutationCompletions.receiveSerialised()
		}
	},
	formatMaterialSelect: () => {
		var ret = []
		for (let i = 0; i < GetMaterials.data.data.queryMaterialDefinition.length; i ++) {
			let line = {
				"label": GetMaterials.data.data.queryMaterialDefinition[i].code,
				"value": GetMaterials.data.data.queryMaterialDefinition[i].id
			}
			ret.push(line)
		}
		return ret
	}
}
