actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[{
         consignmentNumber: inpAddCarrierNum.text,
         order: {
					 	status:"CREATED",
					 	customerOrderNumber: inpAddShipOrderNum.text,
					 	shipFromPartner: {
					 		id:selAddInShipSupplier.selectedOptionValue
				 		}
					},
					warehouse:{
						id:selectWarehouse.selectedOptionValue
					},
					type:"INBOUND"
     	}]
		},
    query: `
     mutation ($input: [AddShipmentInput!]!) {
         addShipment(input: $input) {
				 	shipment{
						id
					}
         }
     }
    `
})
}}

