actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[{
        jobOrder:{id:tblJobOrderList.selectedRow.id},
				material:{id:selectMaterialToAdd.selectedOptionValue},
				quantity:parseFloat(inpMaterialQuantityToAdd.text) 
     	}]
		},
    query: `
     mutation ($input: [AddMaterialRequirementInput!]!) {
         addMaterialRequirement(input: $input) {
						materialRequirement{
							id
						}
         }
     }
    `
})
}}

