actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[{
        code: inpJobOrderCodeReceiving.text,
				customer:{id:selectCustomer.selectedOptionValue},
				equipment:{id:selectWarehouse.selectedOptionValue},
				operationsType: "Inventory",
				orderType:"PURCHASE_ORDER",
				operationsDefinition:{name:"ReceiveStock"},
				quantity:1,
				quantityUoM:{code:"EA"},
				requestState:{name:"Unscheduled"},
				segmentRequirements:[{
					code:inpJobOrderCodeReceiving.text+"-receive",
					segment:{id:selectSegment.selectedOptionValue},
					jobOrders:[{
						name:inpJobOrderCodeReceiving.text+"-receive-"+moment().format(),
						dispatchStatus:"ACTIVE",
						equipment:{id:selectWarehouse.selectedOptionValue},
						workType: "Inventory",
						jobResponses:[{
							startDateTime:moment()
						}]
					}]
				}]
     	}]
		},
    query: `
     mutation ($input: [AddOperationsRequestInput!]!) {
         addOperationsRequest(input: $input) {
				 	operationsRequest{
						id
					}
         }
     }
    `
})
}}

