actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[{
				code:inpJobOrderCodeReceiving.text+"-receive-"+moment().format(),
				operationsRequest:{id:appsmith.store.operationsRequestId},
				segment:{id:selectSegment.selectedOptionValue},
				jobOrders:[{
					name:inpJobOrderCodeReceiving.text+"-receive-"+moment().format(),
					dispatchStatus:"ACTIVE",
					equipment:{id:selectWarehouse.selectedOptionValue},
					workType: "Inventory",
					jobResponses:[{
						startDateTime:moment()
					}]
				}]
			}]
		},
    query: `
     mutation ($input: [AddSegmentRequirementInput!]!) {
         addSegmentRequirement(input: $input) {
						segmentRequirement{
							id
						}
         }
     }
    `
})
}}

