actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			jobOrderInput:{
				filter:{id:[tblJobOrderList.selectedRow.id]},
				set:{dispatchStatus:"COMPLETE"}
			},
			jobResponseInput:{
				filter:{id:[tblJobOrderList.selectedRow.jobResponses[0].id]},
				set:{endDateTime:moment()}
			}
		},
    query: `
mutation CompleteJobOrder($jobOrderInput:UpdateJobOrderInput!,$jobResponseInput:UpdateJobResponseInput!){
	updateJobOrder(input:$jobOrderInput){
		jobOrder{
			id
		}
	}
  updateJobResponse(input:$jobResponseInput){
    jobResponse{
			id
		}
  }
}
    `
})
}}

