actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			jobOrderInput:{
				filter:{id:[tblJobOrderList.selectedRow.id]},
				set:{dispatchStatus:"COMPLETE"}
			}
		},
    query: `
mutation CompleteJobOrder($jobOrderInput:UpdateJobOrderInput!){
	updateJobOrder(input:$jobOrderInput){
		jobOrder{
			id
		}
	}
}
    `
})
}}

