actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			code:selectMaterialRequirement.selectedOptionValue,
			warehouse:selectWarehouse.selectedOptionLabel
		},
    query: `
query GetBulkStatus($code:String!,$warehouse:String!) {
  getMaterialDefinition(code:$code) @cascade{
    code
    inventoryHandlingPolicy {
      rules {
        warehouse (filter:{name:{eq:$warehouse}}) {
          name
        }
        stateModel {
          states (filter:{canReceive:true}){
            canReceive 
            status {
							value:id
              label:code
            }
          }
        }
      }
    }
  }
}
    `
})
}}

