actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:tblJobOrderList.selectedRow.id
		},
    query: `
query($id:ID!){
  getJobOrder(id:$id){
		jobResponses {
			id
		}
		segmentRequirement {
			id
			materialRequirements {
				id
				material{
					code,
					name,
					description
					inventoryHandlingPolicy{
						trackBySerialNumber
						trackByCarrier
					}
				},
				materialSubLots {
					id
					code
				}
			}
		}
    materialRequirements{
			id
			material{
				code,
				name,
				description
				inventoryHandlingPolicy{
					trackBySerialNumber
					trackByCarrier
				}
			},
			quantity
		}
  }
}
    `
})
}}

