actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectWarehouse.selectedOptionValue
		},
    query: `
query($id:ID!){
  getEquipment(id:$id){
    name
    shipments(filter:{type:{eq:INBOUND},not:{has:dateTimeReceivedUTC}}){
      id
      order{id,customerOrderNumber,status,shipFromPartner{name}}
      consignmentNumber
			owner{name}
    }
  }
}
    `
})
}}

