actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectWarehouse.selectedOptionValue
		},
    query: `
query($id:ID!){
  getEquipment(id:$id){
    name
    jobOrders(filter:{workType:{eq:Inventory},has:segmentRequirement}){
			id
      name
			dispatchStatus
      segmentRequirement{
        code
        operationsRequest{
          code
          customer{name}
        }

      }
			jobResponses(filter:{not:{has:endDateTime}}){
				id
				startDateTime
			}
    }
  }
}
    `
})
}}

