actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			equipmentId:selectWarehouse.selectedOptionValue,
			customerId:selectCustomer.selectedOptionValue
		},
    query: `
query($equipmentId:ID!, $customerId:ID!){
  getEquipment(id:$equipmentId){
    name
		operationsRequests(filter:{operationsType:{eq:Inventory}}) @cascade(fields:["customer","segmentRequirements"]){
      code
      customer (filter:{id:[$customerId]}){id}
      segmentRequirements @cascade(fields:"jobOrders"){
        code
        jobOrders(filter:{dispatchStatus:{in:[CREATED,ACTIVE]}}){
          id
          createdDateTime
          name
          dispatchStatus
        }
      }
    }
  }
}
    `
})
}}

