actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			equipment:selectWarehouse.selectedOptionLabel,
			customer:selectCustomer.selectedOptionLabel
		},
    query: `
query($customer:String!,$equipment:String!){
  queryJobOrder(filter:{dispatchStatus:{in:[CREATED,ACTIVE]}})@cascade(fields:["equipment"]){
    name
    id
    equipment(filter:{name:{eq:$equipment}}){
      name
    }
    createdDateTime
    dispatchStatus
    segmentRequirement{
				code
      segment{
        name
      }
      operationsRequest{
        code
        customer(filter:{name:{eq:$customer}}){
							id
          name
        }
      }
    }
    materialRequirements{
      material{
        code
      }
      quantity
    }
    jobResponses{
      id
      startDateTime
    }
  }
}
    `
})
}}

