actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectWarehouse.selectedOptionValue
		},
    query: `
query($id:ID!){
  queryMaterialDefinition @cascade{
    id
    code
    name
    description
    inventoryHandlingPolicy{
      rules{
        warehouse(filter:{id:[$id]}){
          id
        }
      }
			trackByCarrier
			trackBySerialNumber
    }
  }
}
    `
})
}}

