actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			customerId:selectCustomer.selectedOptionValue,
			warehouseId:selectWarehouse.selectedOptionValue
		},
    query: `
query queryShipments($customerId:ID!, $warehouseId:ID!){
  getPartner(id:$customerId){
    shipments @cascade(fields:"warehouse"){
			id
			consignmentNumber
			customerReference
			warehouse(filter:{id:[$warehouseId]}){
				id
			}
			itemsAggregate{count}
		}
  }
}
    `
})
}}

