actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:tblShipmentList.selectedRow.id,
			warehouseId:selectWarehouse.selectedOptionValue
		},
    query: `
query getShipmentItems($id:ID!, $warehouseId:ID!){
  getShipment(id:$id){
    id
    warehouse{id,name}
    customerReference
		order {
			shipFromPartner {
				id
			}
		}
    consignmentNumber
    from {
      name
    }
    type
    items{
      id
      status
      material{
        code 
        name
        description
				serialMask
        inventoryHandlingPolicy @cascade(fields:["rules"]){
          rules{
            warehouse(filter:{id:[$warehouseId]}){
              name
            }
          }
          trackByCarrier
          trackBySerialNumber
          verifySerialNumberOnReceipt
        }
      }
      plannedQuantity
    }
  }
}
    `
})
}}

