actionConfiguration.body:
(materialId) => {
		if (!_.isEmpty(materialId)){
			GetShipmentItemDetail.run()
			storeValue('receiveTabsDefault',Receive.shipmentItemReceiveType())
			.then(()=> {
				resetWidget('receiveTabs',true)
			})
			GetBulkStatus.run()
		} else {
			GetShipmentItemDetail.run()
			for (let i = 0; i < GetJobOrderMaterialReqs.data.data.getJobOrder.materialRequirements.length; i++) {
				let line = GetJobOrderMaterialReqs.data.data.getJobOrder.materialRequirements[i].material
				if (line.code == selectMaterialRequirement.selectedOptionValue) {
					if (line.inventoryHandlingPolicy.trackBySerialNumber != null) {
						if (line.inventoryHandlingPolicy.trackBySerialNumber == true) {
							storeValue('receiveTabsDefault',"SERIALIZED")
								.then(()=> {
								resetWidget('receiveTabs',true)
							})
							return "serialized"
						}
					}
					if (line.inventoryHandlingPolicy.trackByCarrier != null) {
						if (line.inventoryHandlingPolicy.trackByCarrier == true) {
							storeValue('receiveTabsDefault',"CARRIER")
								.then(()=> {
								resetWidget('receiveTabs',true)
							})
							return "carrier"
						}
					}
					storeValue('receiveTabsDefault',"BULK")
						.then(()=> {
						resetWidget('receiveTabs',true)
					})
					return "bulk"
				}
			}

		}

	}

