actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: [
    {
      material: {code: selectMaterialRequirement.selectedOptionValue},
			materialSubLot: {code: inpBarcode.text},
      materialUse: "Received",
			jobResponse: {id:tblJobOrderList.selectedRow.jobResponses[0].id},
      owner: {id: selectCustomer.selectedOptionValue},
			user: {id: GetUserSites.data.data.getUser.id},
      quantity: 1,
      from: {
        shipment: {id: },
        shipmentItem: {id: }
      },
      to: {
        carrier: {id: serialSelectCarrier.selectedOptionValue},
      }
    }
  ]
		},
    query: `
mutation ReceiveSerialised($input:[AddInventoryTransactionInput!]!) {
	addInventoryTransactions(input:$input){
    material{code},
    storageLocation{name},
    quantity
  }
}
    `
})
}}

