actionConfiguration.body:
() => {
		// validate the required fields. We need an owner, a warehouse, an Order Number and a Segment
		if ( _.isNull(inpJobOrderCodeReceiving.text) || _.isNull(selectCustomer.selectedOptionValue) || _.isNull(selectWarehouse.selectedOptionValue) || _.isNull(selectSegment.selectedOptionValue)){
			showAlert("One of the required inputs is missing.We need an owner, a warehouse, an Order Number and a Segment ")
			return
		}
		//check if the order already exists. If it does, add a new segmentRequirement and JobOrder, otherwise add the whole OperationsRequest
		return GetOperationsRequest.run()
			.then(()=>{
						if (GetOperationsRequest.data.errors != null) {
							showAlert(ReceiveSerialised.data.errors[0].message)
							return
						} else {
							if ( _.isNull( GetOperationsRequest.data.data.getOperationsRequest)) {
									return AddOperationsRequestReceiving.run()
									.then(()=>{
										if (AddOperationsRequestReceiving.data.errors != null) {
											showAlert(AddOperationsRequestReceiving.data.errors.message)
										}
										return
									})
							} else {
									storeValue("operationsRequestId",GetOperationsRequest.data.data.getOperationsRequest.id)
									return AddSegmentRequirement.run()
									.then(()=>{
										if (AddSegmentRequirement.data.errors != null) {
											showAlert(AddSegmentRequirement.data.errors.message)
										}
											return
									})
							}
						}
				})
			.then(()=>{
			return GetJobOrdersToReceive.run()
			.then(()=>{
				if (GetJobOrdersToReceive.data.errors != null) {
							showAlert(ReceiveSerialised.data.errors[0].message)
							return
				} else {				
					closeModal("ModalAddReceivingJob")
					showModal("ModalSelectJobOrder")
				}
			})
		})
	}

