actionConfiguration.body:
() => {
		// we need a material and a quantity
		if ( _.isNull(selectMaterialToAdd.selectedOptionValue) || _.isNull(inpMaterialQuantityToAdd.text)){
			showAlert("One of the required inputs is missing.We need a Material and a quantity ")
			return
		}	else {
			return AddMaterialRequirement.run().then(()=>{
				if (AddMaterialRequirement.data.errors != null) {
					showAlert(AddSegmentRequirement.data.errors.message)
					return
				} else {
					return GetJobOrderMaterialReqs.run().then(()=>{
						closeModal("modalAddMaterial")
						showAlert("completed successfully")
					})
				}			
			})

		}
	}

