actionConfiguration.body:
() => {
		// validate the required fields. We need a JobOrder.id and a JobResponse.id
		if ( _.isNull(tblJobOrderList.selectedRow.id) ){
			showAlert("One of the required inputs is missing.We need a JobOrder.id and a JobResponse.id ")
			return
		}	else {
			return CompleteJobOrder.run().then(()=>{
				if (CompleteJobOrder.data.errors != null) {
					showAlert(AddSegmentRequirement.data.errors.message)
					return
				} else {
					return GetJobOrdersToReceive.run().then(()=>{
						resetWidget("tblJobOrderList")
						showAlert("completed successfully")
					})
				}			
			})

		}
	}

