actionConfiguration.body:
() => {
    let result = [];
    GetJobOrdersToReceive.data.data.getEquipment.operationsRequests.forEach((oReq)=>{
			oReq.segmentRequirements.forEach((segReq)=>{
				segReq.jobOrders.forEach((jobOrder)=>{
					let item = {
						id: jobOrder.id,
						operationsRequestCode:oReq.code,
						segmentRequirementCode: segReq.code,
						customerId: oReq.customer.id,
						createdDateTime: jobOrder.createdDateTime,
						dispatchStatus: jobOrder.dispatchStatus,
						jobResponseId: _.isEmpty(jobOrder.jobResponses)? "" : jobOrder.jobResponses[0].id
					}
					result.push(item)
				})		
			})
		}) 
    return result;
	}

