actionConfiguration.body:
() => {
    let result = [];
    if(GetJobOrdersToReceiveNew.data.data.queryJobOrder.length > 0){
			for (let i = 0; i < GetJobOrdersToReceiveNew.data.data.queryJobOrder.length; i++){
				let jobOrder = GetJobOrdersToReceiveNew.data.data.queryJobOrder[i]
				let line = {
					id: jobOrder.id,
					operationsRequestCode: jobOrder.segmentRequirement.operationsRequest.code,
					segmentRequirementCode: jobOrder.segmentRequirement.code,
					customerId:jobOrder.segmentRequirement.operationsRequest.customer.id,
					createdDateTime: jobOrder.createdDateTime,
					dispatchStatus:jobOrder.dispatchStatus,
					jobResponseId: _.isEmpty(jobOrder.jobResponses)? "" : jobOrder.jobResponses[0].id
				}
				result.push(line)
			}
		}
    return result;
	}

