actionConfiguration.body:
() => {
		if (inpBarcode.text == ""){
			return false
		}
		if (GetJobOrderMaterialReqs.data.data.getJobOrder.segmentRequirement == null){
			return false
		}
		if (GetJobOrderMaterialReqs.data.data.getJobOrder.materialRequirements == null){
			return false
		}
		var serialList = []
		for (let i = 0; i < GetJobOrderMaterialReqs.data.data.getJobOrder.materialRequirements.length; i ++) {
			let line = GetJobOrderMaterialReqs.data.data.getJobOrder.segmentRequirement.materialRequirements[i]
			if (line.materialSubLots != null) {
				for (let j = 0; j < line.materialSubLots.length; j++) {
					serialList.push(line.materialSubLots[j].code)
				}
			}
		}
		return _.includes(serialList, inpBarcode.text)
	}

