export default {
	myVar1: [],
	myVar2: {},
	formatQueryRequests:async () => {
		//write code here
		let ret = [];
		if(QueryOperationsRequest.data.data.queryOperationsRequest.length > 0){
			for( let i = 0; i < QueryOperationsRequest.data.data.queryOperationsRequest.length; i++){
				for (let j = 0; j < QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements.length; j++){
					for (let k = 0; k < QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements[j].materialRequirements.length;k++){
						storeValue("materialCode",QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements[j].materialRequirements[k].material.code);
						storeValue("partnerCode",QueryOperationsRequest.data.data.queryOperationsRequest[i].customer.name);
						QueryPartners.run();
							let line = {
										"customerName": QueryOperationsRequest.data.data.queryOperationsRequest[i].customer.name,
										"orderNumber": QueryOperationsRequest.data.data.queryOperationsRequest[i].code,
										"materialCode" : QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements[j].materialRequirements[k].material.code,
										"segmentCode": QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements[j].code,
										"quantity": QueryOperationsRequest.data.data.queryOperationsRequest[i].segmentRequirements[j].materialRequirements[k].quantity
									};
							ret.push(line);
					}
				}
			}
		}
		return ret;
	},
}
