actionConfiguration.body:
{{
    JSON.stringify({
      variables: {
        Equipment: selectSite.selectedOptionLabel,
        State: "In Cart",
      },
      query: `
    query($Equipment:String!,$State:String!){
      queryOperationsRequest@cascade{
        code
						customer{
							name
						}
        equipment(filter:{name:{eq:$Equipment}}){
          name
        }
        requestState(filter:{name:{eq:$State}}){
          name
        }
        segmentRequirements{
          code
          materialRequirements{
            material{
              code
            }
            quantity
          }
        }
      }
    }
        `})
  }}

