actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			material: appsmith.store.materialCode,
			partner:appsmith.store.partnerCode,
			site: selectSite.selectedOptionLabel
		},
    query: `
query($partner:String!,$material:String!,$site:String!){
  queryPartner(filter:{name:{eq:$partner}}){
    name
    inventoryHandlingRules{
      warehouse@cascade{
        name
        parent(filter:{name:{eq:$site}}){
          name
        }
      }
      inventoryHoldingPolicy@cascade{
        material(filter:{code:{eq:$material}}){
          code
        }
					inventoryMax
					inventoryMin
      }
      inventoryHandlingPolicy{
        id
        material(filter:{code:{eq:$material}}){
          code
        }
      }
    }
  }
}
    `
})
}}

