actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			material: appsmith.store.materialCode,
			partner:"ZELLER"
		},
    query: `
query($partner:String!,$material:String!){
  queryPartner(filter:{name:{eq:$partner}})@cascade{
    name
    inventoryHandlingRules{
      warehouse{
        name
        parent{
          name
        }
      }
      inventoryHandlingPolicy{
        material(filter:{code:{eq:$material}}){
          code
        }
      }
    }
  }
}
    `
})
}}

