actionConfiguration.body:
()=>{
		let tableData = appsmith.store.InCartData;
		let ret = []
		for (let i = 0; i < tableData.length; i++){
			let line = tableData[i]
			let temp = {
				name:line.segmentRequirementName+"/"+line.EquipmentName,
				equipment:{
					name:line.EquipmentName,
				},
				materialRequirements:{
					material:{
						code:line.materialCode
					},
					quantity:line.plannedQuantity
			},
				segmentRequirement:{
					code:line.segmentRequirementName+"/"+line.EquipmentName+"segmentRequirement",
					operationsRequest:{
						id:line.requestId
					},
					segment:{
						id:line.segmentID
					}
				},
				isActive:true
			}
			ret.push(temp)
		}
		return ret
	}

