actionConfiguration.body:
() => {
		//write code here
		let ret = appsmith.store.InCartData
		let quantity = ret[ASNTable.selectedRowIndex].quantity
		let difference = ret[ASNTable.selectedRowIndex].plannedQuantity - _.toInteger(InputPlanQuantity.text)
		let materialCode = ret[ASNTable.selectedRowIndex].materialCode
		let orderNumber = ret[ASNTable.selectedRowIndex].segmentRequirementName
		let currentlyPlanned = 0
		for (let i = 0; i < ret.length; i++){
			if (ret[i].materialCode == materialCode && ret[i].segmentRequirementName == orderNumber){
				currentlyPlanned = currentlyPlanned - ret[i].plannedQuantity
			}
		}
		let quantityNow = quantity + currentlyPlanned + difference
		for (let i = 0; i < ret.length; i++){
			if (ret[i].materialCode == materialCode && ret[i].segmentRequirementName == orderNumber){
				ret[i].AvailableStock = quantityNow
			}
		}
		ret[ASNTable.selectedRowIndex].plannedQuantity = _.toInteger(InputPlanQuantity.text)
		storeValue("InCartData",ret)
		closeModal("ModalPlanStock")
		return ret
	}

