actionConfiguration.body:
() => {
	let tableData = appsmith.store.InCartData;
	let idList = []
	for (let i = 0; i < tableData.length; i++){
			let line = tableData[i]
			idList.push(line.requestId)
	}
	let ret = {
				filter:{
					id:idList
				},
				set:{
					requestState:{
						id:"0x5fa15"
					}
				}
			}
	return ret
}

