actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:selectSite.selectedOptionValue,
			input2:selectSite.selectedOptionValue
		},
    query: `
query QueryEquipment($input:[ID!]!, $input2:String!){
  queryEquipment (filter:{id:$input}){
    id
    orders: stagedOrders(filter:{and:{isActive:true, orderType:{eq:PURCHASE_ORDER}, not:{has:shipments}}}){
			customerOrderNumber
	id
		shipments{id}
      lines{

				quantity
        material{
          code
          materialEquipment(filter:{siteID:[$input2]}){
            equipment{
              name 
            }
            planningSummaryByState(filter:{canReceive:true}){
              state {
                status {
                  code
                }
              }
              inventoryMin
              inventoryMax
              stockOnHand
            }
          }
        }
      }
    }
  }
}
    `
})
}}

