actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:TableASNStaging.tableData,
			site:selectSite.selectedOptionValue,
			partner:{id:selectCustomer.selectedOptionValue},
			opDef:{id:selectOperationsDefinition.selectedOptionValue}
		},
    query: `
mutation NotConfirmASN($input:[ConfirmASNInput!]!,$site:String!,$partner:ExtPartnerRef!,$opDef:ExtOperationsDefinitionRef!){
  confirmASN(input:$input,siteID:$site,PartnerRef:$partner,OperationsDefinitionRef:$opDef) {
			multipleEquipment
    Requests {
			id
		}
  }
}
    `
})
}}

