actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[{
         "material":{"id":selectAddInShipItemMaterial.selectedOptionValue},
				"plannedQuantity":parseInt(inpAddInShipItemQty.text),
				"shipment":{"id":appsmith.store.selectedShipment.id},
				"status":"CREATED"
     }]
		},
    query: `
     mutation ($input: [AddShipmentItemInput!]!) {
         addShipmentItem(input: $input) {
				 	shipmentItem{
						id
					}
         }
     }
    `
})
}}

