    variables: {
			id:listInboundDeliveries.selectedItem.id
		},
    query: `
query GetDelivery($id:ID!){
  getDelivery(id:$id) {
    partner{name}
    transportCompany
		transportCompanyDeliveryId
    dateTime
    shipments {
			id
      consignmentNumber
      transportCompanyShipmentId
      customerReference
    }
  }
}
    `
})
}}

