actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:listShipments.selectedItem.id
		},
    query: `
query GetShipmentDetail($id:ID!) {
  getShipment(id:$id) {
    order {
      customerOrderNumber
    } 
    consignmentNumber
    items {
			id
      plannedQuantity,
      material{id, code},
      bin{id, name}
    }
  }
}
    `
})
}}

