actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectSite.selectedOptionValue
		},
    query: `
query($id:ID!){
	getEquipment(id:$id){
		id
		name
		children(filter:{equipmentLevel:{eq:Warehouse},not:{isActive:false}}){
			value:id
			label:name
			children (filter:{equipmentLevel:{eq:StorageZone}}) {
      	value:id
      	label:name
    	}
		}
		orders (filter:{status:{eq:CREATED},or:{status:{eq:RECEIVED}},and:{orderType:{eq:PURCHASE_ORDER}}}) {
      value:id
      label:customerOrderNumber
    }
	}
}
    `
})
}}

